INSERT INTO vacancies (id, name, description, status) VALUES
(1, 'Химик-технолог', 'Разработка новых составов электропроводящих красок и покрытий, улучшение существующих рецептур. Подбор компонентов, тестирование образцов, анализ результатов. Требуются знание химии полимеров, лакокрасочных материалов и умение работать с лабораторным оборудованием.', 'Свободна'),
(2, 'Специалист по антикоррозионным покрытиям', 'Подбор и внедрение защитных покрытий для предотвращения коррозии металлов. Участие в разработке методик испытаний, контроль качества наносимых покрытий. Необходимо понимание основ коррозионных процессов и владение методами анализа состояния поверхностей.', 'Свободна'),
(3, 'Инженер-конструктор', 'Проектирование изделий с применением электропроводящих покрытий. Разработка чертежей, моделей и технических заданий. Умение работать с CAD-системами (AutoCAD, SolidWorks) и знание материаловедения.', 'Свободна'),
(4, 'Менеджер по продажам', 'Продажа продукции ЭПФ клиентам, консультирование по применению электропроводящих и антикоррозионных покрытий. Активный поиск клиентов, ведение переговоров, заключение договоров. Необходимы навыки активных продаж и коммуникабельность.', 'Свободна'),
(5, 'Логист', 'Организация поставок сырья и готовой продукции, взаимодействие с поставщиками и транспортными компаниями. Контроль сроков доставки, оформление сопроводительной документации. Требуется опыт работы в логистике и умение вести переговоры.', 'Свободна'),
(6, 'Экономист', 'Проведение экономического анализа деятельности предприятия, разработка планов развития производства, расчет себестоимости продукции. Организация учета затрат и доходов, составление отчетности. Нужны хорошие знания экономики предприятий и бухгалтерского учета.', 'Свободна'),
(7, 'Маркетолог', 'Исследование рынков сбыта, продвижение продукции компании ЭПФ, привлечение новых заказчиков. Планирование рекламных кампаний, работа с сайтом и социальными сетями. Необходимо уметь анализировать рынок и разрабатывать маркетинговые стратегии.', 'Свободна'),
(8, 'Техник-лаборант', 'Выполнение лабораторных анализов и испытаний материалов, оценка качества продукции. Оформление протоколов испытаний, подготовка отчетов. Должны иметь навыки работы с химическими реактивами и лабораторным оборудованием.', 'Свободна'),
(9, 'Начальник отдела контроля качества', 'Контроль качества выпускаемой продукции, проверка соблюдения технологических регламентов. Организация работы лаборатории, проведение инспекций и аттестация сотрудников. Требуется опыт управления персоналом и знание нормативных документов.', 'Свободна'),
(10, 'Специалист по сертификации', 'Оформление разрешительных документов на продукцию компании, взаимодействие с сертифицирующими органами. Консультации по вопросам стандартизации и технического регулирования. Необходимы знания ГОСТов, технических условий и процедур сертификации.', 'Свободна'),
(11, 'Руководитель производственного участка', 'Руководство производственным процессом, контроль соблюдения технологического процесса, повышение производительности труда. Решение вопросов планирования и оперативного управления участком. Нужно хорошее знание производственных процессов и опыт руководства коллективом.', 'Свободна'),
(12, 'Инженер по оборудованию', 'Эксплуатация и обслуживание промышленного оборудования, ремонт и модернизация установок. Диагностика неисправностей, составление заявок на закупку запчастей. Желательно наличие инженерного образования и опыт работы с технологическим оборудованием.', 'Свободна'),
(13, 'Эколог', 'Осуществление экологического мониторинга, подготовка экологической документации, соблюдение природоохранного законодательства. Организация мероприятий по снижению негативного влияния на окружающую среду. Важно владеть методиками оценки воздействия на природу и знать законодательные нормы.', 'Свободна'),
(14, 'Специалист по техдокументации', 'Подготовка и сопровождение технической документации (технические условия, паспорта качества, инструкции по эксплуатации). Составление сертификатов и согласование документации с контролирующими органами. Необходимо знание норм оформления документации и способность быстро осваивать новые виды работ.', 'Свободна'),
(15, 'Специалист по работе с клиентами', 'Предоставление консультаций клиентам по выбору покрытий, прием заказов, обработка претензий. Поддержание контактов с постоянными заказчиками, ведение клиентской базы. Важны отличные навыки коммуникации и стрессоустойчивость.', 'Свободна'),
(16, 'Инженер-проектировщик', 'Разработка проектной документации на нанесение электропроводящих покрытий. Проектирование схем расположения покрытий, подбор методов нанесения. Навык работы с AutoCAD, ArchiCAD и знание технологии нанесения покрытий.', 'Свободна'),
(17, 'Главный бухгалтер', 'Организационная деятельность финансовой службы предприятия, управление налоговым учетом, бюджетирование расходов. Выполнение расчетов заработной платы, подготовка налоговой отчетности. Высшее экономическое образование обязательно.', 'Свободна'),
(18, 'Менеджер проектов', 'Планирование и координация проекта внедрения нового продукта или технологии. Управление ресурсами, сроки исполнения и бюджетом проекта. Способность эффективно организовывать работу команды и контролировать исполнение этапов.', 'Свободна'),
(19, 'Дизайнер упаковки', 'Разработка дизайна упаковочной продукции, этикеток и маркировок для товаров компании. Творческое мышление, умение создавать привлекательные макеты упаковки и соблюдать фирменный стиль компании.', 'Свободна'),
(20, 'Администратор склада', 'Организация складского хозяйства, учет запасов материалов и готовых изделий, выдача товара покупателям. Ответственность за сохранность материальных ценностей, своевременное пополнение запасов. Желателен опыт работы в складском хозяйстве.', 'Свободна');